// Contact.js
import React from 'react';

const Contact = () => {
  return (
    <div className="contact bg-white py-12 px-4">
  <div className="max-w-6xl mx-auto text-center">
    {" "}
    {/* Add text-center here to center the contents */}
    <div className="mb-8">
      {" "}
      {/* Removed contact-info and hours classes */}
      <h2 className="text-3xl font-bold mb-4">Contact Us</h2>
      <p className="mb-4">Cancellation Policy</p>
      <p className="mb-8">
        Cancellations accepted up to 24 hours before the scheduled service.
        Within 24 hours of the service, cancellations will be charged 50% of the
        scheduled cost.
      </p>
      <button className="whatsapp-button py-2 px-4 bg-green-500 text-white font-bold rounded-full inline-flex items-center justify-center">
        {" "}
        {/* Add inline-flex and justify-center */}
        <i className="fab fa-whatsapp mr-2" /> Message us on WhatsApp
      </button>
    </div>
    <div className="mb-8">
      {" "}
      {/* Removed contact-info and hours classes */}
      <p className="footer-text mb-4">Hours</p>
      <select className="border border-gray-300 rounded-lg p-2">
        <option>Open today 09:00 am - 05:00 pm</option>
      </select>
    </div>
  </div>
</div>

  );
};

export default Contact;
