// Gallery.js
import React from 'react';

const Gallery = () => {
  return (
    <div className="gallery py-12 px-4 text-center">
  <h2 className="text-4xl section-title mb-6">
    Beauty Beyond The Surface: A Gallery of Our Salon's Creativity and Artistry
  </h2>
  {/* Carousel Container */}
  <div className="relative">
    <img
      src="https://placehold.co/1200x500"
      alt="Salon interior with orange chairs and modern design"
      className="max-w-full h-auto rounded-lg shadow-lg"
    />
    {/* Left Arrow Button */}
    <button className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-white p-2 rounded-full">
      <i className="fas fa-chevron-left" />
    </button>
    {/* Right Arrow Button */}
    <button className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-white p-2 rounded-full">
      <i className="fas fa-chevron-right" />
    </button>
  </div>
</div>

  );
};

export default Gallery;
